<style>
	header, form, main, .p24, .text {
		display: flex;
	}
	header {
		align-items: stretch;
		min-height: 300px;
		background: var(--text);
		gap: 8px;
	}
	form {
		align-items: stretch;
		width: 100%;
	}
	label, button, input {
		font-weight: 900;
		font-size: 64px;
	}
	button {
		padding: 0 24px;
	}
	main {
		border-top: 8px solid var(--text);
		gap: 24px;
		padding: 24px;
		align-items: center;
	}
	.p24 {
		justify-content: center;
		padding: 24px;
		background: black;
	}
	.fc {
		flex-direction: column;
	}
	.flx {
		flex: 1;
	}
	.text {
		justify-content: center;
		align-items: center;
		gap: 24px;
	}
	a {
		font-size: 100px;
		padding-bottom: 8px;
		padding-inline: 48px;
		width: fit-content;
		margin: auto;
	}
	input {
		text-align: center;
		width: 300px;
		text-transform: uppercase;
		border-bottom: 8px solid var(--text);
		background: transparent;
		color: var(--text);
	}
	svg {
		display: block;
		width: 100%;
	}
	@media (max-width: 1000px) {
		a {
			font-size: 64px;
		}
		.text {
			flex-direction: column;
		}
		label {
			text-align: center;
		}
	}
	@media (max-width: 700px) {
		.pablo {
			display: none;
		}
		a {
			font-size: 32px;
		}
		header, form {
			flex-direction: column;
		}
		header {
			min-height: auto;
		}
		.text {
			gap: 0;
		}
		form {
			gap: 32px;
		}
		p {
			text-align: center;
		}
		label, button, input {
			font-size: 32px;
		}
	}
</style>